#include <iostream>
#include <vector>
#include <map>
#include <utility>
#include <cmath>

using namespace std;


class Solution {
public:
    int majorityElement(vector<int> &num) {
		//create hashmap
		int result = 0;
		map<int,int> f;
		
		for(auto i = num.begin(); i != num.end(); i++){
			if(f[*i]==0){
				f[*i]=1;
			}else{
				f[*i]=f[*i]+1;
			}
		}
		
		
		
		for(auto  j = f.begin();j != f.end(); j++){
			pair<int,int> t = *j;
			
			int value = t.second;
			
			if(value>=ceil(num.size()/2.0)){
				
				if(value>=result){ 
					result= t.first;
					}
			} 
		}
		return result;
		
		
    }
};

